import React, { useEffect, useState } from 'react';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function MyOrder() {
    const [orderData, setOrderData] = useState([]); // Initialize as an empty array

    const fetchMyOrder = async () => {
        console.log("Fetching orders for:", localStorage.getItem('userEmail'));
        try {
            const response = await fetch("http://localhost:5000/api/auth/myOrderData", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: localStorage.getItem('userEmail')
                })
            });
            const data = await response.json();
            console.log("Order Data received from API:", data); // Debugging line
            setOrderData(data);
        } catch (error) {
            console.error("Error fetching order data:", error);
        }
    }

    useEffect(() => {
        fetchMyOrder();
    }, []);

    return (
        <div>
            <Navbar />
            <div className='container'>
                <div className='row'>
                    {orderData.length > 0 ? (
                        orderData.orderData.order_data.slice(0).reverse().map((item, index) => (
                            <div key={index}>
                                {item.map((arrayData, idx) => (
                                    <div key={idx}>
                                        {arrayData.Order_date ? (
                                            <div className='m-auto mt-5'>
                                                <hr />
                                                <strong>{arrayData.Order_date}</strong>
                                            </div>
                                        ) : (
                                            <div className='col-12 col-md-6 col-lg-3'>
                                                <div className="card mt-3" style={{ width: "16rem", maxHeight: "360px" }}>
                                                    <img src={arrayData.img} className="card-img-top" alt="Product" style={{ height: "120px", objectFit: "fill" }} />
                                                    <div className="card-body">
                                                        <h5 className="card-title">{arrayData.name}</h5>
                                                        <div className='container w-100 p-0' style={{ height: "38px" }}>
                                                            <span className='m-1'>{arrayData.qty}</span>
                                                            <span className='m-1'>{arrayData.size}</span>
                                                            <span className='m-1'>{arrayData.Order_date}</span>
                                                            <div className='d-inline ms-2 h-100 w-20 fs-5'>
                                                                â‚¹{arrayData.price}/-
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        )}
                                    </div>
                                ))}
                            </div>
                        ))
                    ) : (
                        <div>No orders found</div>
                    )}
                </div>
            </div>
            <Footer />
        </div>
    );
}
